import axios from 'axios';
import { createAsyncThunk, createEntityAdapter, createSlice } from "@reduxjs/toolkit";


export const postProject = createAsyncThunk('projects/postProject', async (body) => {
    const response = await axios.post('http://localhost:3500/projects', body);
    return response.data.project
})

export const patchProject = createAsyncThunk('projects/patchProject', async (body) => {
    const response = await axios.patch('http://localhost:3500/projects', body);
    return response.data.project
})

export const deleteProject = createAsyncThunk('projects/deleteProject', async (body) => {
    const response = await axios.delete('http://localhost:3500/projects', { data: body });
    return response.data
})


const projectsSelector = createEntityAdapter()

const initialState = projectsSelector.getInitialState({
    status: 'idle',
    error: null
})

const projectsSlice = createSlice({
    name: 'projects',
    initialState,
    reducers: {},

    extraReducers(builder) {
        builder
            .addCase(fetchProjects.pending, (state, action) => {
                state.status = 'loading'
            })
            .addCase(fetchProjects.fulfilled, (state, action) => {
                state.status = 'succeeded'
                projectsSelector.setAll(state, action.payload.map((project) => (
                    { ...project, id: project._id })))
            })
            .addCase(fetchProjects.rejected, (state, action) => {
                state.status = 'failed'
                state.error = action.error.message
            })
            .addCase(postProject.fulfilled, (state, action) => {
                state.status = 'succeeded'
                projectsSelector.addOne(state, {...action.payload, id: action.payload._id})
            })
            .addCase(patchProject.fulfilled, (state, action) => {
                state.status = 'succeeded'
                projectsSelector.setOne(state, {...action.payload, id: action.payload._id})
            })
            .addCase(deleteProject.fulfilled, (state, action) => {
                state.status = 'succeeded'
                projectsSelector.removeOne(state, action.payload._id)
            })
    }
})

export const {
    selectAll: selectAllProjects,
    selectById: selectProjectById
} = projectsSelector.getSelectors(state => state.projects)

export default projectsSlice.reducer;
