import { useState } from "react"
import { projectUpdated } from "./projectsSlice"
import { useDispatch } from "react-redux"
import { useParams } from 'react-router-dom'
import { useSelector } from "react-redux"
import { Navigate } from "react-router-dom"

const EditProject = () => {
    const Id = useParams().projectId;
    const project = useSelector(state => state.projects.find(project => project.id === Id))

    const [title, setTitle] = useState(project.title)
    const [content, setContent] = useState(project.content)

    const dispatch = useDispatch()

    const onSaveProjectClicked = () => {
        dispatch(projectUpdated(Id, title, content))
        Navigate(`/projects/${Id}`)
    }

    return (
        <section>
            <h2>Edit a New Project</h2>
            <form className="project__form">
                <label htmlFor="projectTitle">Project Title:</label>
                <input type="text" id="projectTitle" name="projectTitle" value={title} onChange={(e) => { setTitle(e.target.value) }} />

                <label htmlFor="projectContent">Content:</label>
                <textarea id="projectContent" name="projectContent" value={content} onChange={(e) => { setContent(e.target.value) }} />

                <button type="button" className="project__button" onClick={onSaveProjectClicked} >
                    Save Post
                </button>
            </form>
        </section>
    )
}
export default EditProject
