import { createSlice } from "@reduxjs/toolkit"
import { nanoid } from "@reduxjs/toolkit"
import axios from 'axios';
import { createAsyncThunk } from "@reduxjs/toolkit";


const initialState = {
    allProjects: [
        { id: '1', title: 'First Post!', content: 'Hello!' },
        { id: '2', title: 'Second Post', content: 'More text' }
    ],
    status: 'idle',
    error: null
}

export const fetchPosts = createAsyncThunk('projects/fetchProjects', async () => {
    const response = await axios.get('http://localhost:3500/projects');
    con
    return response.data
  })


const projectsSlice = createSlice({
    name: 'projects',
    initialState,
    reducers: {
        projectAdded: {
            reducer: (state, action) => {
                state.allProjects.push(action.payload)
            },
            prepare: (title, content) => {
                return {
                    payload: {
                        id: nanoid(),
                        title,
                        content
                    }
                }
            }
        },
        projectUpdated: {
            reducer: (state, action) => {
                const existingProject = state.allProjects.find(project => project.id === action.payload.id)
                if (existingProject) {
                    existingProject.title = action.payload.title
                    existingProject.content = action.payload.content
                }

            },
            prepare: (id, title, content) => {
                return {
                    payload: {
                        id,
                        title,
                        content
                    }
                }
            }
        },

    }
})

export const selectAllProjects = (state) => state.projects.allProjects;
export const selectProjectById = (state, Id) => state.projects.allProjects.find(project => project.id === Id)

// action creater, which is automatically created with the same name as reducer
export const { projectAdded, projectUpdated } = projectsSlice.actions;

export default projectsSlice.reducer;
