import axios from 'axios';
import { createAsyncThunk, createEntityAdapter, createSlice } from "@reduxjs/toolkit";


export const postProject = createAsyncThunk('projects/postProject', async (body) => {
    const response = await axios.post('http://localhost:3500/projects', body);
    return response.data
})

export const fetchProjects = createAsyncThunk('projects/fetchProjects', async () => {
    const response = await axios.get('http://localhost:3500/projects');
    return response.data.projects
})


const projectsSelector = createEntityAdapter()

const initialState = projectsSelector.getInitialState({
    status: 'idle',
    error: null
})

const projectsSlice = createSlice({
    name: 'projects',
    initialState,
    reducers: {
        projectUpdated: {
            reducer: (state, action) => {
                const existingProject = state.entities[action.payload._id]
                if (existingProject) {
                    existingProject.title = action.payload.title
                    existingProject.summary = action.payload.summary
                }

            },
            prepare: (_id, title, summary) => {
                return {
                    payload: { _id, title, summary }
                }
            }
        },
    },

    extraReducers(builder) {
        builder
            .addCase(fetchProjects.pending, (state, action) => {
                state.status = 'loading'
            })
            .addCase(fetchProjects.fulfilled, (state, action) => {
                state.status = 'succeeded'
                projectsSelector.upsertMany(state, action.payload.map((project) => (
                    { ...project, id: project._id })))
            })
            .addCase(fetchProjects.rejected, (state, action) => {
                state.status = 'failed'
                state.error = action.error.message
            })
            .addCase(postProject.fulfilled, (state, action) => {
                state.status = 'succeeded'
                projectsSelector.addOne(state, {...action.payload.project, id: action.payload.project._id})
            })
    }
})

export const {
    selectAll: selectAllProjects,
    selectById: selectProjectById
} = projectsSelector.getSelectors(state => state.projects)


// action creater, which is automatically created with the same name as reducer
export const { projectUpdated } = projectsSlice.actions;

export default projectsSlice.reducer;
