import { useState } from "react"
import { projectUpdated } from "./projectsSlice"
import { useDispatch } from "react-redux"
import { useParams } from 'react-router-dom'
import { useSelector } from "react-redux"
import { useNavigate } from "react-router-dom"
import { selectProjectById } from "./projectsSlice";

const EditProject = () => {
    const Id = useParams().projectId;
    const project = useSelector(state => selectProjectById(state, Id))

    const [title, setTitle] = useState(project.title)
    const [summary, setSummary] = useState(project.summary)

    const dispatch = useDispatch()
    const navigate = useNavigate()

    const onSaveProjectClicked = () => {
        dispatch(projectUpdated(Id, title, summary))
        setTitle('')
        setContent('')
        navigate(`/project/${Id}`)
    }

    return (
        <main>
            <h2>Edit a New Project</h2>
            <form className="project__form">
                <label htmlFor="projectTitle">Project Title:</label>
                <input type="text" id="projectTitle" name="projectTitle" value={title} onChange={(e) => { setTitle(e.target.value) }} />

                <label htmlFor="projectContent">summary:</label>
                <textarea id="projectContent" name="projectContent" value={content} onChange={(e) => { setContent(e.target.value) }} />

                <button type="button" className="project__button" onClick={onSaveProjectClicked} >
                    Save Post
                </button>
            </form>
        </main>
    )
}
export default EditProject
