import { useSelector, useDispatch } from "react-redux";
import { selectAllProjects, fetchPosts } from "./projectsSlice";
import { Link } from "react-router-dom";
import { useEffect } from "react";
import AddProject from "./AddProject";
import './Projects.css'

const ProjectList = () => {
    const projects = useSelector(selectAllProjects)
    const dispatch = useDispatch()





    const renderedProjects = projects.map(project => (
        <article key={project.id} className="project__article">
            <h3 className="project__title">{project.title}</h3>
            <p className="project__content">{project.content.substring(0, 100)}</p>
            <div className="project__links">
                <Link to={`/project/${project.id}`} className="project__link">
                    View Post
                </Link>
                <Link to={`/editproject/${project.id}`} className="project__link">
                    Edit Post
                </Link>
            </div>
        </article>
    ))

    return (
        <main>
            <h1>
                ProjectList
            </h1>

            <AddProject />
            {renderedProjects}
        </main>
    )
}
export default ProjectList
