import { createSlice } from "@reduxjs/toolkit"
import { nanoid } from "@reduxjs/toolkit"
import axios from 'axios';
import { createAsyncThunk } from "@reduxjs/toolkit";


const initialState = {
    allProjects: [],
    status: 'idle',
    error: null
}

export const postProject = createAsyncThunk('projects/postProject', async())

export const fetchProjects = createAsyncThunk('projects/fetchProjects', async () => {
    const response = await axios.get('http://localhost:3500/projects');
    await new Promise(r => setTimeout(r, 1000));
    return response.data
})


const projectsSlice = createSlice({
    name: 'projects',
    initialState,
    reducers: {
        projectAdded: {
            reducer: (state, action) => {
                state.allProjects.push(action.payload)
            },
            prepare: (title, summary) => {
                return {
                    payload: { _id: nanoid(), title, summary }
                }
            }
        },
        projectUpdated: {
            reducer: (state, action) => {
                const existingProject = state.allProjects.find(project => project._id === action.payload._id)
                if (existingProject) {
                    existingProject.title = action.payload.title
                    existingProject.summary = action.payload.summary
                }

            },
            prepare: (_id, title, summary) => {
                return {
                    payload: { _id, title, summary }
                }
            }
        },

    },
    extraReducers(builder) {
        builder
            .addCase(fetchProjects.pending, (state, action) => {
                state.status = 'loading'
            })
            .addCase(fetchProjects.fulfilled, (state, action) => {
                state.status = 'succeeded'
                state.allProjects = action.payload
            })
            .addCase(fetchProjects.rejected, (state, action) => {
                state.status = 'failed'
                state.error = action.error.message
            })
    }
})

export const selectAllProjects = (state) => state.projects.allProjects;
export const selectProjectById = (state, _id) => state.projects.allProjects.find(project => project._id === _id)

// action creater, which is automatically created with the same name as reducer
export const { projectAdded, projectUpdated } = projectsSlice.actions;

export default projectsSlice.reducer;
