import { useState } from "react"
import { postProject } from "./projectsSlice"
import { useDispatch } from "react-redux"

const AddProject = () => {
  const [title, setTitle] = useState('')
  const [content, setContent] = useState('')
  const [saveStatus, setSaveStatus] = useState('idle')

  const dispatch = useDispatch()

  const onSaveProjectClicked = async () => {
    setSaveStatus('pending')
    await dispatch(projectAdded(title, content))
    setSaveStatus('success')
  }

  return (
    <section>
      <h2>Add a New Project</h2>
      <form className="project__form">
        <label htmlFor="projectTitle">Project Title:</label>
        <input type="text" id="projectTitle" name="projectTitle" value={title} onChange={(e) => {setTitle(e.target.value)}} />

        <label htmlFor="projectContent">Content:</label>
        <textarea id="projectContent" name="projectContent" value={content} onChange={(e) => {setContent(e.target.value)}} />

        <button type="button" className="project__button" onClick={onSaveProjectClicked} >
          Save Post
        </button>
      </form>
    </section>
  )
}
export default AddProject
