import { useSelector, useDispatch } from "react-redux";
import { selectAllProjects, fetchProjects } from "./projectsSlice";
import { Link } from "react-router-dom";
import { useEffect } from "react";
import AddProject from "./AddProject";
import './Projects.css'

const ProjectList = () => {
    const projects = useSelector(selectAllProjects)
    const dispatch = useDispatch()

    const projectsStatus = useSelector(state => state.projects.status)
    const projectData = useSelector(state => state.projects.allProjects)

    useEffect(() => {
        console.log("run", projectsStatus, projectData)
        if (projectsStatus === 'idle') {
            dispatch(fetchProjects())
        }
    }, [projectsStatus, dispatch])


    const renderedProjects = projects.map(project => (
        <article key={project.id} className="project__article">
            <h3 className="project__title">{project.title}</h3>
            <p className="project__content">{project.summary.substring(0, 100)}</p>
            <div className="project__links">
                <Link to={`/project/${project.id}`} className="project__link">
                    View Project
                </Link>
                <Link to={`/editproject/${project.id}`} className="project__link">
                    Edit Project
                </Link>
            </div>
        </article>
    ))

    return (
        <main>
            <h1>
                ProjectList
            </h1>

            <AddProject />
            {renderedProjects}
        </main>
    )
}
export default ProjectList
