import { useSelector, useDispatch } from "react-redux";
import { selectAllProjects, fetchProjects } from "./projectsSlice";
import { Link } from "react-router-dom";
import { useEffect } from "react";
import AddProject from "./AddProject";
import './Projects.css'

const ProjectList = () => {
    const projects = useSelector(selectAllProjects)
    const dispatch = useDispatch()

    const projectsStatus = useSelector(state => state.projects.status)

    const deleteProjectClicked = async () => {
        try {
            dispatch(patchProject({ id, title, summary })).unwrap()
            setTitle('')
            setSummary('')
            navigate(`/projects`)
        } catch (err) {
            console.log(err)
        }
    }

    useEffect(() => {
        if (projectsStatus === 'idle') {
            dispatch(fetchProjects())
        }
    }, [projectsStatus, dispatch])

    const renderedProjects =
        projectsStatus === 'loading' ?
            <h1>Loading...</h1> :
            projects.map(project => (
                <article key={project._id} className="project__article">
                    <h3 className="project__title">{project.title}</h3>
                    <p className="project__content">{project.summary.substring(0, 100)}</p>
                    <div className="project__links">
                        <Link to={`/project/${project._id}`} className="project__link">
                            View Project
                        </Link>
                        <Link to={`/editproject/${project._id}`} className="project__link">
                            Edit Project
                        </Link>
                        <button type="button" className="project__link" onClick={onSaveProjectClicked} >
                            Save Post
                        </button>
                    </div>
                </article>
            ))

    return (
        <main>
            <h1>
                ProjectList
            </h1>

            <AddProject />
            {renderedProjects}
        </main>
    )
}
export default ProjectList
