import { createSlice } from "@reduxjs/toolkit"
import { nanoid } from "@reduxjs/toolkit"


const initialState = [
    { id: '1', title: 'First Post!', content: 'Hello!' },
    { id: '2', title: 'Second Post', content: 'More text' }
  ]


const projectsSlice = createSlice({
    name: 'projects',
    initialState,
    reducers: {
        projectAdded: {
            reducer: (state, action) => {
                state.push(action.payload)
            },
            prepare: (title, content) => {
                return {
                    payload: {
                        id: nanoid(),
                        title,
                        content
                    }
                }
            }
        },
        projectUpdated: {
            reducer: (state, action) => {
                const existingProject = state.find(project => project.id === action.payload.id)
                if (existingProject) {
                    project.title = action.payload.title
                    project.content = action.payload.content
                }

            },
            prepare: (id, title, content) => {
                return {
                    payload: {
                        id,
                        title,
                        content
                    }
                }
            }
        },

    }
})

export const selectAllProjects = (state) => state.projects;

export const { projectAdded } = projectsSlice.actions;  // action creater, which is automatically created with the same name as reducer

export default projectsSlice.reducer;
