import { useDispatch } from "react-redux";
import { deleteProject } from "./projectsSlice";
import { useGetProjectsQuery } from "../api/apiSlice";
import { Link } from "react-router-dom";
import AddProject from "./AddProject";
import './Projects.css'

const ProjectList = () => {
    const dispatch = useDispatch()
    const deleteProjectClicked = async (id) => {
        try {
            dispatch(deleteProject({ id })).unwrap()
        } catch (err) {
            console.log(err)
        }
    }

    const {
        data: projects,
        isLoading,
        isSuccess,
        isError,
        error
      } = useGetProjectsQuery()

    const renderedProjects = isLoading ? <h1>Loading...</h1> :

            projects.map(project => (
                <article key={project._id} className="project__article">
                    <h3 className="project__title">{project.title}</h3>
                    <p className="project__content">{project.summary.substring(0, 100)}</p>
                    <div className="project__links">
                        <Link to={`/project/${project._id}`} className="project__link">
                            View Project
                        </Link>
                        <Link to={`/editproject/${project._id}`} className="project__link">
                            Edit Project
                        </Link>
                        <button type="button" className="project__link" onClick={() => deleteProjectClicked(project._id)} >
                            Delete Post
                        </button>
                    </div>
                </article>
            ))

    return (
        <main>
            <h1>
                ProjectList
            </h1>

            <AddProject />
            {renderedProjects}
        </main>
    )
}
export default ProjectList
