import { createSlice, createAsyncThunk } from "@reduxjs/toolkit"
import { nanoid } from "@reduxjs/toolkit"
import axios from "axios"


const POSTS_URL = "http://localhost:3500/projects/get"

const initialState = {
    projects: [],
    status: 'idle',
    error: null
}

export const fetchProjects = createAsyncThunk('projects/fetchProjects/get', async () => {
    const response = await axios.get(POSTS_URL)
    console.log(response)
    return response.data
})


const projectsSlice = createSlice({
    name: 'projects',
    initialState,
    reducers: {
        projectAdded: {
            reducer: (state, action) => {
                state.projects.push(action.payload)
            },
            prepare: (title, content) => {
                return {
                    payload: {
                        id: nanoid(),
                        title: title,
                        content: content
                    }
                }
            }
        }
    },
    extraReducers(builder) {
        builder
            .addCase(fetchProjects.pending, (state, action) => {
                state.status = 'loading'
            })
            .addCase(fetchProjects.fulfilled, (state, action) => {
                state.status = 'succeeded'
                state.posts = state.posts.concat(action.payload)
            })
            .addCase(fetchProjects.rejected, (state, action) => {
                state.status = 'failed'
                state.error = action.error.message
            })
            .addCase(fetchProjects.fulfilled, (state, action) => {
                // Fix for API post IDs:
                // Creating sortedPosts & assigning the id 
                // would be not be needed if the fake API 
                // returned accurate new post IDs
                const sortedPosts = state.posts.sort((a, b) => {
                    if (a.id > b.id) return 1
                    if (a.id < b.id) return -1
                    return 0
                })
                action.payload.id = sortedPosts[sortedPosts.length - 1].id + 1;
                // End fix for fake API post IDs 

                action.payload.userId = Number(action.payload.userId)
                action.payload.date = new Date().toISOString();
                action.payload.reactions = {
                    thumbsUp: 0,
                    hooray: 0,
                    heart: 0,
                    rocket: 0,
                    eyes: 0
                }
                console.log(action.payload)
                state.posts.push(action.payload)
            })}
})

export const selectAllProjects = (state) => state.projects;

export const { projectAdded } = projectsSlice.actions;

export default projectsSlice.reducer;
