import { createSlice, createAsyncThunk } from "@reduxjs/toolkit"
import { nanoid } from "@reduxjs/toolkit"
import axios from "axios"


const initialState = {
    projects: [],
    status: 'idle',
    error: null
}

export const fetchProjects = createAsyncThunk('projects/fetchProjects', async () => {
    const response = await axios.get(POSTS_URL)
    return response.data
})


const projectsSlice = createSlice({
    name: 'projects',
    initialState,
    reducers: {
        projectAdded: {
            reducer: (state, action) => {
                state.projects.push(action.payload)
            },
            prepare: (title, content) => {
                return {
                    payload: {
                        id: nanoid(),
                        title: title,
                        content: content
                    }
                }
            }
        }
    },

    extraReducers(builder) {
        builder
            .addCase(fetchProjects.pending, (state, action) => {
                state.status = 'loading'
            })
            .addCase(fetchProjects.fulfilled, (state, action) => {
                state.status = 'succeeded'
                const loadedPosts = action.payload
                state.projects = state.projects.concat(loadedPosts)
            })
            .addCase(fetchProjects.rejected, (state, action) => {
                state.status = 'failed'
                state.error = action.error.message
            })

    }
})

export const selectAllProjects = (state) => state.projects.projects;
export const getProjectsStatus = (state) => state.projects.status;
export const getProjectsError = (state) => state.projects.error;

export const { projectAdded } = projectsSlice.actions;

export default projectsSlice.reducer;
